import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";
import Typewriter from "typewriter-effect";

const Home = () => {
    const [data, setData] = useState(null);
    const sectionRefs = React.useRef([]);
    const [showSection, setShowSection] = useState(false);


    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.home, { method: "GET" });
                const res = await response.json();
                setData(res);
                setShowSection(true);
            } catch (err) {
                console.error("Home fetch Error", err);
            }
        };
        fetchData();


    const handleScroll = () => {
        sectionRefs.current.forEach((sectionRef, index) => {
          const sectionTop = sectionRef.getBoundingClientRect().top;
          const sectionBottom = sectionRef.getBoundingClientRect().bottom;
          const isVisible = sectionTop < window.innerHeight && sectionBottom >= 0;

          if (isVisible) {
            sectionRef.classList.add('animate-in');
          } else {
            sectionRef.classList.remove('animate-in');
          }
        });
      };

      window.addEventListener('scroll', handleScroll);
      return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const sectionRef = (ref) => {
      if (ref) {
        sectionRefs.current.push(ref);
      }
    };

    return data ? (
        <div id="/" className="home">
            <div>
            <img src={data.profilePic.source} alt="ProfilePic"/>
            </div>
            <div className={`home`}>
            <h1 className="name">{data.name}</h1>
            <div className="textanimation">
                <h2 className="im">I'm</h2>
                <span>&nbsp;</span>
                <Typewriter
                    options={{
                        strings: data.roles,
                        autoStart: true,
                        loop: true
                    }}
                />
            </div>
            <div className="section" ref={sectionRef}>
           <div className="home-paragraph"><p>{data.paragraph}</p></div>
           </div>
            </div>

        </div>
    ) : (<FallbackSpinner />)
}

export default Home
