    setShowModal(false);
  };

  const fullBullets = () => {
    return(
      bullets.map((Text, index) => {
        return (
          <ul key={index}>
          <li>{Text}</li>
          </ul>
        );

      })
    )
  }

  const twoBullets = () => {
    return (
      <ul>
        <li>{bullets[0]}</li>
        <li>{bullets[1]}....</li>
      </ul>
    );
  }

  const regularText = () => {
    return (
      <p>{bullets}</p>
    );
  }

  return (
    <div className="container">

      {sectionType?.image &&
      <Image className="image" 
      src={sectionType?.image} 
      onClick={() => handleImageClick(sectionType?.image)} 
      alt="ProjectImage" />}

      <h3>{sectionType.title}</h3>

      {sectionType.bodyText &&  (
          bullets.length > 2 ? 
           (<div>
            {showMore ? fullBullets() : twoBullets()}
          <Button variant="primary" onClick={toggleShowMore}>
            {showMore ? "Show Less" : "Show More"}
          </Button>
           </div>
           ) : (
            <div className="regularText">
              {regularText()}

            </div>
           )
      )
      }

      {sectionType.links && (
        <div className="links">
          {sectionType?.links?.map((link, index) => (
            <a
              key={index}
              href={link.href}
              target="_blank"
              rel="noopener noreferrer"
            >
              {link.text}
            </a>
          ))}
        </div>
      )}

      <div className="tags-container">
        {sectionType?.tags?.map((tag, index) => (
          <span key={index} className="badge">
            {tag}
          </span>
        ))}
      </div>

      <div className="footerUrl">
      {sectionType.credentialUrl &&
         <a
          href={sectionType?.credentialUrl}
          target="_blank"
          rel="noopener noreferrer"
        >
          Verify Credential
        </a>}
      </div>
      <Modal size="lg" show={showModal} onHide={handleCloseModal}>

      <Modal.Body>
        <Image src={selectedImage} fluid />
      </Modal.Body>
    </Modal>
    </div>
  );
};

export default DefaultContainers;
