import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";

const Home = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.home, { method: "GET" });
                const res = await response.json();
                setData(res);
            } catch (err) {
                console.error("Home fetch Error", err);
            }
        };
        fetchData();
    }, []);

    return data? (
        <div id="home" className="home">
        <img src={data.profilePic.source} alt="ProfilePic" 
        style={{width: data.profilePic.width, 
            height: data.profilePic.height, 
            borderRadius: data.profilePic["border-radius"], 
            objectFit: data.profilePic["object-fit"], 
            marginBottom: data.profilePic["margin-bottom"]}}/>
        <h1>{data.name}</h1>
        <div className="roles">
            {data.roles.map((role, index) => <p key={index}>{role}</p>)}
        </div>
        <p>{data.paragraph.map((para, index) => <p key={index}>{para}</p>)}</p>
    </div>
    ) : (<FallbackSpinner />)
}

export default Home
