import NavBar from './components/NavigationBar';
import React, { useState, useEffect, lazy, Suspense } from 'react';
import FallbackSpinner from './components/FallbackSpinner';
import endpoints from './constants/endpoints';
import { Routes, Route, Navigate } from 'react-router-dom'; // Import Routes and Route

const MainApp = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(endpoints.routes, { method: "GET" });
        const res = await response.json();
        setData(res);
      } catch (err) {
        console.error("Routes fetch Error", err);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="MainApp">
      <NavBar />
      <section className="main">
        <Routes>
          <Route path="/" element={<Navigate to="/home" replace />} />
          {data &&
            data.sections &&
            data.sections.map((section, index) => {
              const Component = lazy(() => import(`./components/${section.component}`));

              return (
                <Route
                  key={section.headerTitle}
                  path={section.path}
                  element={
                    <Suspense fallback={<FallbackSpinner />}>
                      <Component header={section.headerTitle} />
                    </Suspense>
                  }
                />
              );
            })}
        </Routes>
      </section>
    </div>
  );
};
