import React, { useState, useEffect, useRef } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";

const NavBar = () => {
    const [data, setData] = useState(null);
    const navRef = useRef(null);

    useEffect(() => {
        fetch(endpoints.navbar, { method: "GET" })
            .then((res) => res.json())
            .then((res) => setData(res))
            .catch((err) => console.error("Navbar fetch Error", err));

    }, []);

    useEffect(() => {
        let prevScrollPos = window.scrollY;
        const handleScroll = () => {
            const currentScrollPos = window.scrollY
            const navbar = navRef.current;
            if (navbar) { // Check if navbar exists before accessing style
              (prevScrollPos > currentScrollPos) ? navbar.style.transform="translateY(0)" : navbar.style.transform="translateY(-200%)";
            }
            prevScrollPos = currentScrollPos;
          };

        window.addEventListener("scroll", handleScroll);
        return () => {
          window.removeEventListener("scroll", handleScroll);
        }
    }, []);


    return data?(
        <div className="navbar">
            {data.sections && data.sections.map((link, index ) => (
            <ul key={index}>
                <li><a href={link.href}>{link.title}</a></li>
            </ul>
            ))}
        </div>
    ) : (<FallbackSpinner/>);
}

export default NavBar
