import React, { useState } from "react";
import "./DefaultContainers.css";

const DefaultContainers = (props) => {
  const { sectionType } = props;
  const [showFullText, setShowFullText] = useState(false);

  const handleShowMore = () => {
    setShowFullText(true);
  };

  const handleShowLess = () => {
    setShowFullText(false);
  };


  const shouldShowMoreLess = sectionType.bodyText.split(/\r\n|\r|\n/).length > 2;

  return (
    <div className="container">
      {sectionType.image && (
        <img className="image" src={sectionType?.image} alt="ProjectImage" />
      )}
      <h3>{sectionType.title}</h3>
      {sectionType.bodyText && (
        <>
          {showFullText ? (
            sectionType.bodyText.split(/\r\n|\r|\n/).map((Text, index) => (
              <ul key={index}>
                <li>{Text}</li>
              </ul>
            ))
          ) : (
            <>
              <ul>
                <li>{sectionType.bodyText.split(/\r\n|\r|\n/)[0]}</li>
              </ul>
              <ul>
                <li>{ectionType.bodyText.split(/\r\n|\r|\n/)[1]}</li>
              </ul>
              {shouldShowMoreLess && (
                <button onClick={handleShowMore}>Show More</button>
              )}
            </>
          )}
          {showFullText && shouldShowMoreLess && (
            <button onClick={handleShowLess}>Show Less</button>
          )}
        </>
      )}
      <div className="tags-container">
        {sectionType.tags.map((tag, index) => (
          <span key={index} className="badge">
            {tag}
          </span>
        ))}
      </div>
    </div>
  );
};

export default DefaultContainers;
