import React, { useState, useEffect, lazy, Suspense, useRef } from 'react';
import NavBar from './components/NavigationBar';
import FallbackSpinner from './components/FallbackSpinner';
import endpoints from './constants/endpoints';

const MainApp = () => {
  const [data, setData] = useState(null);
  const [showSection, setShowSection] = useState(false);
  const sectionRef = useRef([]);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(endpoints.routes, { method: "GET" });
        const res = await response.json();
        setData(res);
      } catch (err) {
        console.error("Routes fetch Error", err);
      }
    };

    fetchData();
    const handleScroll = () => {
      sectionRef.current.forEach((section, index) => {
        const sectionTop = sectionRef.getBoundingClientRect().top;
        const sectionBottom = sectionRef.getBoundingClientRect().bottom;
        const isVisible = sectionTop < window.innerHeight && sectionBottom > 0;
        if (isVisible) {
          sectionRef.classList.add('animate-in');
        }
        else {
          sectionRef.classList.remove('animate-in');
        }
      })
    }

    window.addEventListener('scroll', handleScroll);

  }, []);

  return (
    <div className="MainApp">
      <NavBar />
      <section className="main">
        <div className="sections">
        {data &&
          data.sections &&
          data.sections.map((section, index) => {
            const Component = lazy(() => import(`./components/${section.component}`));

            return (
              <Suspense key={section.headerTitle} fallback={<FallbackSpinner />}>
                <div className="section_container" id={section.path}>
                  <Component header={section.headerTitle} />
                </div>
              </Suspense>
            );
          })}
          </div>
      </section>
    </div>
  );
};

export default MainApp;
