import React, { useState, useEffect } from 'react';


import FallbackSpinner from './components/FallbackSpinner';
import endpoints from './constants/endpoints';

const MainApp = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.routes, { method: "GET" });
                const res = await response.json();
                setData(res);
            } catch (err) {
                console.error("Routes fetch Error", err);
            }
        };
        fetchData();
    }, []);

    const RouteComponent = ({ component }) => {
        const Component = React.lazy(() => import(`./components/${component}`));
        return (
            <React.Suspense fallback={<FallbackSpinner />}>
                <Component />
            </React.Suspense>
        );
    };

    return (
        <>
            <NavBar />
            <main>
                {data ? data.sections.map((route) => (
                    <div key={route.path}>
                        <RouteComponent component={route.component} />
                    </div>
                )) : <FallbackSpinner />}
            </main>
        </>
    );
}

export default MainApp;
