import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";
import Header from "./Header";
import { VerticalTimeline, VerticalTimelineElement }  from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

const Education = (props) => {
    const [data, setData] = useState(null);
    const {header}= props;
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.education, { method: "GET" });
                const res = await response.json();
                setData(res.education);
            } catch (err) {
                console.error("Education fetch Error", err);
            }
        };
        fetchData();
    }, []);

    return (
        <div id="/education">
        <Header title={header} />
        {data ? (
        <VerticalTimeline>
            {data.map((edu, index) => {
                return (
                <VerticalTimelineElement
                    className="vertical-timeline-element--work"
                    contentStyle={{ background: 'rgb(33, 150, 243)', color: '#fff' }}
                    contentArrowStyle={{ borderRight: '7px solid  rgb(33, 150, 243)' }}
                    date={edu.date}
                    iconStyle={{ background: 'rgb(33, 150, 243)', color: '#fff' }}
                    icon={edu.icon}
                >
                <h3 className="vertical-timeline-element-title">{edu.title}</h3>
                <h4 className="vertical-timeline-element-subtitle">{edu.subtitle}</h4>
                <p>
                    {edu.description}
                </p>
                </VerticalTimelineElement>
                );
            })}
        </VerticalTimeline>
        ) : (
            <FallbackSpinner />
        )}
        </div>
    );
};

export default Education;
