import React, { useState, useEffect, createContext, useContext } from "react";

const ThemeContext = createContext({ theme: "light", toggleTheme: () => { } });
export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState(localStorage.getItem('theme') || "light");
    const toggleTheme = () => {
        const newTheme = theme === "light" ? "dark" : "light";
        setTheme(newTheme); localStorage.setItem('theme', newTheme);
    };
    useEffect(() => {
        const handleSystemThemeChange = () => {
            const prefersColorScheme = window.matchMedia('(prefers-color-scheme: dark)');
            setTheme(prefersColorScheme.matches ? 'dark' : 'light'); // Respect system preference initially 
        }; window.addEventListener('storage', handleSystemThemeChange); // Listen for theme changes in localStorage 
        if (theme === 'dark') {
            document.documentElement.style.setProperty('--background-color', 'none');
            document.documentElement.style.setProperty('--text-color', 'white');
            document.documentElement.style.setProperty('--background-image', 'url("https://www.google.com/url?sa=i&url=https%3A%2F%2Fmedium.com%2F%40eggradients%2Fdark-gradient-fbaaf5c8b443&psig=AOvVaw2AENlRRmQaCcP5gy3B9Qq-&ust=1718094227540000&source=images&cd=vfe&opi=89978449&ved=0CBAQjRxqFwoTCPiS-f3N0IYDFQAAAAAdAAAAABAU")');
        }
        else {
            document.documentElement.style.setProperty('--background-color', 'white');
            document.documentElement.style.setProperty('--text-color', 'black');
            document.documentElement.style.setProperty('--background-image', 'none');
        }

        return () => {
            window.removeEventListener('storage', handleSystemThemeChange);
        };
    }, [theme]);
    return (
        <ThemeContext.Provider value={{ theme, toggleTheme }}>

            {children}
        </ThemeContext.Provider>
    );
}
export const useTheme = () => useContext(ThemeContext);
