import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";
import Header from "./Header";
import { VerticalTimeline, VerticalTimelineElement } from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

const Experience = (props) => {
    const [data, setData] = useState(null);
    const { header } = props;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.experiences, { method: "GET" });
                const res = await response.json();
                setData(res);
            } catch (err) {
                console.error("Experience fetch Error", err);
            }
        };
        fetchData();
    }, []);

    return (
        data ? (
            <div id="/experience">
                <Header title={header} />
                <VerticalTimeline
                    animate={true}
                    >
                    {
                        data.experiences.map((experience, index) => {
                            return (
                                <VerticalTimelineElement
                                    key={index}
                                    className="vertical-timeline-element--work"
                                    contentStyle={{ background: '#f7f7f7', color: 'black', }}
                                    contentArrowStyle={{ borderRight: '7px solid  #f7f7f7' }}
                                    date={experience.dateText}
                                    iconStyle={{ background: 'black', color: '#f7f7f7' }}
                                    icon={experience.icon}
                                >
                                    <h3 className="vertical-timeline-element-title">{experience.title}</h3>
                                    <h4 className="vertical-timeline-element-subtitle">{experience.subtitle}</h4>
                                    <p>{experience.workType}</p>
                                    <ul>
                                        {
                                            experience.workDescription.map((description, index) => {
                                                return <li key={index}>{description}</li>
                                            })
                                        }   
                                    </ul>
                                </VerticalTimelineElement>
                                    )
                                    }
                            )
                        }

                </VerticalTimeline>
            </div>
        ) : (
            <FallbackSpinner />
        )
    );
};

export default Experience;
