import React from 'react';


const ScrollTop = () => {
    return (
        <div
            className="scroll-top"
            onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
        >
            <FontAwesomeIcon icon="fa-solid fa-arrow-up" />
        </div>
    );
};

export default ScrollTop;
