import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";
import Switch from "../theme/Switch";

const NavBar = () => {
  const [data, setData] = useState(null);
  const [prevScrollPos, setPrevScrollPos] = useState(window.scrollY);
  const [activeLink, setActiveLink] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(endpoints.navbar, { method: "GET" });
        const res = await response.json();
        // Modify the href property to match the id
        res.sections = res.sections.map((section) => ({
          ...section,
          href: `/${section.href}`,
        }));
        setData(res);
      } catch (err) {
        console.error("Navbar fetch Error", err);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollPos = window.scrollY;
      const navbar = document.querySelector(".navbar");
      if (navbar) {
        if (prevScrollPos > currentScrollPos) {
          navbar.style.transform = "translateY(0%)";
        } else {
          navbar.style.transform = "translateY(-200%)";
        }
        setPrevScrollPos(currentScrollPos);
      }
      const sections = document.querySelectorAll('div[id]');
      let activeSection = '';
      for (const section of sections) {
        if (section && section.getBoundingClientRect) {
          const sectionTop = section.getBoundingClientRect().top + window.scrollY - 100;
          const sectionBottom = sectionTop + section.offsetHeight;
          const isInViewport = currentScrollPos >= sectionTop && currentScrollPos <= sectionBottom;
          if (isInViewport) {
            activeSection = section.id;
            break;
          }
        }
      }
      setActiveLink(activeSection);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [prevScrollPos]);

  const scrollTo = (e, sectionID) => {
    e.preventDefault();
    const section = document.getElementById(sectionID);
    if (section) {
      section.scrollIntoView({ behavior: "smooth", block: 'start' });
      setActiveLink(sectionID);
    }
  };

  return data ? (
    <div className="navbar">
      {data.sections.map((link, index) => (
        <ul key={link.title}>
          <li>
            <a
              href={link.href}
              className={activeLink === link.href ? "active" : ""}
              onClick={(e) => scrollTo(e, link.href)}
            >
              {link.title}
            </a>
          </li>
        </ul>
      ))}
      <Switch />
    </div>
  ) : (
    <FallbackSpinner />
  );
};

export default NavBar;
