import React, { useState, useEffect } from "react";
import endpoints from "../constants/endpoints";
import FallbackSpinner from "./FallbackSpinner";
import Header from "./Header";
import { VerticalTimeline, VerticalTimelineElement } from 'react-vertical-timeline-component';
import 'react-vertical-timeline-component/style.min.css';

const Education = (props) => {
    const [data, setData] = useState(null);
    const { header } = props;
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(endpoints.education, { method: "GET" });
                const res = await response.json();
                setData(res);
            } catch (err) {
                console.error("Education fetch Error", err);
            }
        };
        fetchData();
    }, []);

    return (
        data ? (
            <div id="/education">
                <Header title={header} />
                <div className="timeline">
                    {data.education.map((education, index) => {
                        return (
                            <VerticalTimeline
                                animate={true}
                                lineColor="black"
                                >
                                <VerticalTimelineElement
                                    className="vertical-timeline-element--work"
                                    contentStyle={{ background: '#f7f7f7', color: 'black', 
                                    boxShadow: '0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)',
                                    borderRadius: '10px', width: '400px' }}
                                    contentArrowStyle={{ borderRight: '7px solid  #f7f7f7' }}
                                    date={education.title}
                                    iconStyle={{ background: '#f7f7f7', color: 'black' }}
                                    icon={<img src={education.src} alt=""
                                        className="vertical-timeline-element-icon" />}
                                >
                                    <h3 className="vertical-timeline-element-title">{education.cardTitle}</h3>
                                    <h4 className="vertical-timeline-element-subtitle">{education.cardSubtitle}</h4>
                                </VerticalTimelineElement>
                            </VerticalTimeline>
                        )
                    })
                    }
                </div>
            </div>

        ) : <FallbackSpinner />
    );
};

export default Education;
